ps -ef | grep tomcat- | awk  'BEGIN{print "\n\n\nRestarting tomcat ++++++++++++++++++++\n\n\n"}{ cnt=split($0, arr, " "); for(k=1;k<=cnt;k++){ind=index(arr[k], ch);if(ind>0){execpath=substr(arr[k],length(ch)+1);print "\nKilling  "execpath" --- pid : "$2; execpath=execpath"/bin/"; system("kill -9 "$2); system("sleep 1"); print "\nStarting  "execpath;system("sh "execpath"startup.sh ");}} } END{print "\n\n\nRestart will finish very soon !++++++++++++++++++++\n\n\n"}' ch="-Dcatalina.home=";
